import React from 'react';

import { useState } from 'react';

export default function CervixBeslutsstod() {
  const [state, setState] = useState({
    alder: '',
    hpvTyp: 'negativ',
    persisterandeHPV: 'nej',
    cytologi: 'normal',
    pad: 'ingen',
    swedescore: 'ej utförd',
    vaccination: 'vet ej',
    resultat: ''
  });

  const getRekommendation = () => {
    if (state.pad === 'invasiv cancer' || state.cytologi === 'misstanke invasiv cancer')
      return 'Kolposkopi snarast med SVF-remiss för invasiv cancer.';
    if (state.pad === 'AIS' || state.cytologi === 'AIS')
      return 'Kolposkopi snarast med SVF-remiss för AIS.';
    return 'Ej definierad kombination – diskutera med kolposkopist.';
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-5 bg-gray-50 rounded-xl shadow-lg">
      <h2 className="text-xl font-bold mb-4">Cervixcancer beslutsstöd</h2>
      <input type="number" placeholder="Ålder"
        className="border p-2 w-full mb-2 rounded"
        value={state.alder}
        onChange={(e) => setState({...state, alder: e.target.value})}
      />
      <button
        className="mt-4 bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
        onClick={() => setState({...state, resultat: getRekommendation()})}
      >
        Visa rekommendation
      </button>
      {state.resultat && (
        <div className="mt-4 p-3 bg-green-100 rounded">
          <strong>Rekommendation:</strong>
          <p>{state.resultat}</p>
        </div>
      )}
    </div>
  );
}
